import React from 'react';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      estadoFav: '',
    }
    this.forms
  }

  forms({target}) {
    this.setState({estadoFav: target.value})
  }

render() {
  return(
    <div>
      <h1>Estados e React</h1>
      <form>
        <label>Estado Favorito?</label>
        <textarea name="estadoFavorito" value={this.state.estadoFav} onChange={this.forms} />
      </form>
    </div>
  )
}
}

export default Form
