import React from 'react';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      estadoFav: '',
    }
    this.forms = this.forms.bind(this)
  }

  forms({target}) {
    this.setState({estadoFav: target.value})
    console.log(this)
  }

render() {
  return(
    <div>
      <h1>Estados e React</h1>
      <form>
        <label>Estado Favorito?</label>
        <textarea name="estadoFavorito" value={this.state.estadoFav} onChange={this.forms} />
        <label>seu genero:</label>
        <select>
          <option>Selecione</option>
          <option>Masculino</option>
          <option>Feminino</option>
          <option>Prefiro nao dizer</option>
        </select>
        <label>seu nome:</label>
          <input/>
          <label>:</label>
          <input/>
      </form>
    </div>
  )
}
}

export default Form
