import React from 'react';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      estadoFav: '',
      nome: '',
      email:'',
      genero
    }
    this.forms = this.forms.bind(this)
  }

  forms({ target }) {
    const { name, value } = target
    this.setState({ [name]: value })
    console.log(this)
  }

  render() {
    return (
      <div>
        <h1>Estados e React</h1>
        <form>
          <label>Estado Favorito?</label>
          <textarea name="estadoFav" value={this.state.estadoFav} onChange={this.forms} />
          <label>seu genero:</label>
          <select name="genero" value={this.state.genero} onChange={this.forms}>
            <option>Selecione</option>
            <option>Masculino</option>
            <option>Feminino</option>
            <option>Prefiro nao dizer</option>
          </select>
          <div>
            <label>seu nome:</label>
            <input name="nome" value={this.state.nome} onChange={this.forms} />
            <label>seu email:</label>
            <input name="email" value={this.state.email} onChange={this.forms} />
          </div>
        </form>
      </div>
    )
  }
}

export default Form
