import React from 'react'
import FillForm from './FillForm'
import Estados from './Estados'

class FieldSet extends React.Component {
  constructor() {
    super()
    this.form = this.form.bind(this)
    this.state = {
      nome: '',
      email: '',
      cpf: '',
      endereco:'',
      cidade:'',
      estado:''
    }
  }

  form({ target }) {
    const { name } = target
    const value = target.type === 'checkbox' ? target.checked : target.value
    this.setState({ [name]: value })
  }

  render() {
    return (
      <div>
        <FillForm type="text" text="Nome: " length="40" name="nome" value={this.state.nome} onChange={this.form} />
        <FillForm type="text" text="Email: " length="50" name="email" value={this.state.email} onChange={this.form} />
        <FillForm type="number" text="CPF: " length="11" name="cpf" value={this.state.cpf} onChange={this.form} />
        <FillForm type="text" text="Endereco: " length="200" name="endereco" value={this.state.endereco} onChange={this.form} />
        <FillForm type="text" text="Cidade: " length="28" name="cidade" value={this.state.cidade} onChange={this.form} />
        <Estados  value={this.state.estado} onChange={this.form} />
      </div>
    )
  }
}

export default FieldSet
